const hash = require('string-hash')
const color = require('tinycolor2')

export const gradient = (uid, type = 'diagonal', opacity = 1) => {
    if (uid) {
        const n = hash(uid)
        const c1 = color({h: n % 360, s: 0.95, l: 0.5})
        const c1_ = c1.toHexString()
        const c2 = c1.triad()[1].toHexString()
        const o = opacity

        switch (type) {
            case 'diagonal':
                return `linear-gradient(to top right, ${c1_}, ${c2}, ${o})`

            case 'radial':
                return `radial-gradient(circle, ${c1_}, ${c2}, ${o})`

            case 'horizontal':
                return `linear-gradient(${c1_}, ${c2}, ${o})`

            case 'vertical':
                return `linear-gradient(to right, ${c1_}, ${c2}, ${o})`

            default:
                return `linear-gradient(to top right, ${c1_}, ${c2}, ${o})`
        }
    }

    throw new TypeError('uid is required')
}

function hexToRgbA(hex){
    let c;
    if(/^#([A-Fa-f0-9]{3}){1,2}$/.test(hex)){
        c= hex.substring(1).split('');
        if(c.length === 3){
            c= [c[0], c[0], c[1], c[1], c[2], c[2]];
        }
        c= '0x'+c.join('');
        return 'rgba('+[(c>>16)&255, (c>>8)&255, c&255].join(',')+',1)';
    }
    throw new Error('Bad Hex');
}
