import React from 'react'
import {NotLogged} from "../../views/NotLogged";
import gradient from 'random-gradient'

import './layout.css'

export const Home = () => {
    const bgGradient = { background: randomGradient(0.4) }
    const bgGradient2 = { background: gradient(randomString(18), randomType() ) }
    console.log(randomGradient(0.4))
    return (
        <NotLogged>
            <div className="home-container">
                <div className="post" style={bgGradient}>
                    <time className="time" dateTime="2019-05-31">
                        <span className="news__day">31</span>
                        <span className="news__year">May 2019</span>
                    </time>
                    Post 1
                </div>
                <div className="post" style={bgGradient2}>
                    <time className="time" dateTime="2019-05-31">
                        <span className="news__day">31</span>
                        <span className="news__year">May 2019</span>
                    </time>
                    Post 1
                </div>
            </div>
        </NotLogged>
    );
};


function randomString(length) {
    let result = '';
    const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    const charactersLength = characters.length;
    for (let i = 0; i < length; i++) {
        result += characters.charAt(Math.floor(Math.random() * charactersLength));
    }
    return result;
}

function randomType() {
    const words = ["diagonal", "vertical", "horizontal", "radial"];
    const word = words[Math.floor(Math.random() * words.length)];
    return word
}

function randomGradient(opacity) {
    let val = [];
    let min = Math.ceil(50);
    let max = Math.floor(255);
    for (let i = 0; i <= 6; i++) {
        val.push(Math.floor(Math.random() * (max - min + 1)) + min);
    }
    return 'linear-gradient(rgba(' + val[0] + ', ' + val[1] + ', ' + val[3] + ', ' + opacity + '), ' +
        'rgba(' + val[4] + ', ' + val[5] + ', ' + val[6] + ', ' + opacity + '))'
}
