import React from "react";
import {gradient, randomType} from "../../../services/gradient";

export const Post = props => {

    const bgGradient = { background: gradient(props.title, randomType(), 1)}
    const date = formatDate(props.date)
    // console.log(props.date)
    console.log(date)

    return (
        <div className="post" style={bgGradient}>
            <time className="time">
                <span className="news__day">{props.title}</span>
                <span className="news__year">May 2019</span>
            </time>
            {props.title}
        </div>
    )
}

function formatDate(date) {
    const currentMonth = date.getMonth();
    const monthString = currentMonth >= 10 ? currentMonth : `0${currentMonth}`;
    const currentDate = date.getDate();
    const dateString = currentDate >= 10 ? currentDate : `0${currentDate}`;
    return `${date.getFullYear()}-${monthString}-${currentDate}`;
}
