import React, {useEffect, useRef, useState} from 'react'
import {NotLogged} from "../../views/NotLogged";
import {Post} from "./Post/Post";
import {API} from "../../services/api";
import {List} from "antd";
import './layout.css'


export const Home = () => {

    const postsAmount = 10;
    const [postsRequest, setPostsRequest] = useState({
        postsLoaded: 0,
        posts: []
    });
    const [page, setPage] = useState(1);
    const [loading, setLoading] = useState(true)
    const loader = useRef(null);


    useEffect(() => {
        const observer = new IntersectionObserver(handleObserver);
        if (loader.current) {
            observer.observe(loader.current)
        }
    }, []);

    useEffect(() => {
        API.get(`/post/last/${postsAmount}/${postsRequest.postsLoaded}`)
            .then((response) => {
                if (loading === true) {
                    if (response.data !== []) {
                        setLoading(false)
                    }
                    console.log("weszlo")
                    let newPosts = postsRequest.posts
                    Array.prototype.push.apply(newPosts, response.data)
                    setPostsRequest({
                        postsLoaded: postsRequest.postsLoaded + Object.keys(response.data).length,
                        posts: newPosts
                    })
                }
            })
            .catch(errInfo => console.error(errInfo))
    }, [page])

    const handleObserver = (entities) => {
        const target = entities[0];
        if (target.isIntersecting) {
            setPage((page) => page + 1)
        }
    }

    // function isLoading() {
    //     if (loading === true)
    //         return (
    //
    //         )
    //     else
    //         return null
    // }

    return (
        <NotLogged>
            <div >
                <div className="post-list">
                    {
                        <List
                            dataSource={postsRequest.posts}
                            renderItem={post => (
                                <Post
                                    title={post.title}
                                    date={post.createdAt.$date}
                                    body={post.body}
                                />
                            )}
                        />
                    }
                    {/*{loading === true &&*/}
                        <div className="loading" ref={loader}>
                            <div className="lds-ellipsis"><div/><div/><div/><div/></div>
                        </div>
                    {/*}*/}
                </div>
            </div>
        </NotLogged>
    )

};

