import React, {useEffect, useState} from 'react'
import {NotLogged} from "../../views/NotLogged";
import {Post} from "./Post/Post";
import {API} from "../../services/api";
import {Button, List} from "antd";
import './layout.css'
import InfiniteScroll from 'react-infinite-scroller';

export const Home = () => {

    const [postList, setPostList] = useState([]);
    const [page, setPage] = useState(1);

    useEffect(() => {
        API.get(`/post/last/${amountOfLoadedPosts}/${alreadyLoaded}`)
            .then((response) => {
                let newPosts = posts
                Array.prototype.push.apply(newPosts, response.data)
                console.log(newPosts)
                setPosts(newPosts)
            })
            .catch(errInfo => console.error(errInfo))
    }, [alreadyLoaded]);

    function loadMore() {
        setAlreadyLoaded(alreadyLoaded + amountOfLoadedPosts)
    }

    if (posts === undefined || posts === [] || posts === null){
        return (
            <NotLogged/>
        )
    }
    else {
        return (
            <NotLogged>
                <div>
                    <InfiniteScroll
                        pageStart={0}
                        loadMore={loadMore}
                        hasMore={true}
                        loader={<div className="loader" key={0}>Loading ...</div>}
                        useWindow={false}
                    >
                        <List
                            dataSource={posts}
                            renderItem={post => (
                                <Post
                                    title={post.title}
                                    date={post.createdAt.$date}
                                    body={post.body}
                                />
                            )}
                        />
                    </InfiniteScroll>
                </div>

                {/*<button onClick={loadMore} > </button>*/}

            </NotLogged>
        );
    }
};

