import React, {useEffect, useRef, useState} from 'react'
import {NotLogged} from "../../views/NotLogged";
import {Post} from "./Post/Post";
import {API} from "../../services/api";
import {List} from "antd";
import './layout.css'


export const Home = () => {

    const [postsRequest, setPostsRequest] = useState({postsLoaded: 0, posts: []})
    const [page, setPage] = useState(1);
    // const [postsLoaded, setPostsLoaded] = useState(0)
    const loader = useRef(null);

    useEffect(() => {
        const observer = new IntersectionObserver(handleObserver);
        if (loader.current) {
            observer.observe(loader.current)
        }

    }, []);

    useEffect(() => {
        API.get(`/post/last/10/${posts.postsLoaded}`)
            .then((response) => {
                let newPosts = posts
                Array.prototype.push.apply(newPosts, response.data)
                setPostsRequest(newPosts)
                setPostsLoaded(postsLoaded + 10)
                console.log(newPosts)
                console.log(postsLoaded)
            })
            .catch(errInfo => console.error(errInfo))
    }, [page])

    // console.log(posts)
    // console.log(postsLoaded)

    const handleObserver = (entities) => {


        const target = entities[0];
        if (target.isIntersecting) {
            setPage((page) => page + 1)
        }
    }

    return (
        <NotLogged>
            <div >
                <div className="post-list">
                    {
                        <List
                            dataSource={posts}
                            renderItem={post => (
                                <Post
                                    title={post.title}
                                    date={post.createdAt.$date}
                                    body={post.body}
                                />
                            )}
                        />
                    }

                    <div className="loading" ref={loader}>
                        <h2>Load More</h2>
                    </div>
                </div>
            </div>
        </NotLogged>
    )

};

