import React, {useEffect, useState} from 'react'
import {NotLogged} from "../../views/NotLogged";
import {Post} from "./Post/Post";
import {API} from "../../services/api";
import {Button, List} from "antd";
import './layout.css'
import InfiniteScroll from 'react-infinite-scroller';


export const Home = () => {

    // const [posts, setPosts] = useState([{
    //     _id: {$oid: "000"},
    //     title: 'Template title',
    //     body: 'Template body',
    //     author: {$oid: "000"},
    //     comments: [],
    //     createdAt: {$date: "000"},
    //     modified: false,
    //     lastModifyAt: {$date: "000"},
    // }])

    const amountOfLoadedPosts = 2;
    const [alreadyLoaded, setAlreadyLoaded] = useState(0)
    const [posts, setPosts] = useState([{
        _id: {$oid: "000"},
        title: 'Template title',
        body: 'Template body',
        author: {$oid: "000"},
        comments: [],
        createdAt: {$date: "000"},
        modified: false,
        lastModifyAt: {$date: "000"},
    }])

    useEffect(() => {
        API.get(`/post/last/${amountOfLoadedPosts}/${alreadyLoaded}`)
            .then((response) => {
                let newPosts = posts
                Array.prototype.push.apply(newPosts, response.data)
                setPosts(newPosts)
            })
            .catch(errInfo => console.error(errInfo))
    }, [alreadyLoaded]);

    function loadMore() {
        setAlreadyLoaded(alreadyLoaded + amountOfLoadedPosts)
    }

    if (posts === undefined || posts === [] || posts === null){
        return (
            <NotLogged/>
        )
    }
    else {
        return (
            <NotLogged>
                <div>
                    {/*<InfiniteScroll*/}
                    {/*    pageStart={0}*/}
                    {/*    // loadMore={loadFunc}*/}
                    {/*    hasMore={true}*/}
                    {/*    loader={<div className="loader" key={0}>Loading ...</div>}*/}
                    {/*    useWindow={false}*/}
                    {/*>*/}
                        <List
                            dataSource={posts}
                            renderItem={post => (
                                    <Post
                                        title={post.title}
                                        date={post.createdAt.$date}
                                        body={post.body}
                                    />
                                    )}
                        />
                    {/*</InfiniteScroll>*/}
                </div>
                <button onClick={loadMore} > </button>

            </NotLogged>
        );
    }
};

